package IF.opertors;

public class Task4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 26;

		if (a > 0 && a == 25) {
			System.out.println("Number is in the range 0:25");

		} else if (a > 25 && a < 50) {
			System.out.println("Number is in the range 25:50");

		} else {
			System.out.println("Number is off of range");
		}
	}

}
